import React, { Component } from "react";
import api from "../api";

import styled from "styled-components";

const Title = styled.h1.attrs({
	className: "h1",
})``;

const Wrapper = styled.div.attrs({
	className: "form-group",
})`
	margin: 0 30px;
`;

const Label = styled.label`
	margin: 5px;
`;

const InputText = styled.input.attrs({
	className: "form-control",
})`
	margin: 5px;
`;

const Button = styled.button.attrs({
	className: `btn btn-primary`,
})`
	margin: 15px 15px 15px 5px;
`;

const CancelButton = styled.a.attrs({
	className: `btn btn-danger`,
})`
	margin: 15px 15px 15px 5px;
`;

class SpeakersInsert extends Component {
	constructor(props) {
		super(props);
		this.state = {
            name: "",
            age: "",
            UserName: "",
            email: "",
            Password: "",
            city: "",
            street: "",
            building: "",  
		};
	}

	handleChangeInputName = async event => {
		const name = event.target.value;
		this.setState({ name });
	};

    
	handleIncludeSpeaker = async () => {
		const { name } = this.state;

		const payload = { name,age,UserName,email,Password,city,street,building };

		await api.insertSpeaker(payload).then(res => {
			window.alert(`Speaker inserted successfully`);
			this.setState({
                name: "",
                age: "",
                UserName: "",
                email: "",
                Password: "",
                city: "",
                street: "",
                building: "",
			});
		});
	};

	render() {
		const { name } = this.state;
		return (
			<Wrapper>
				<Title> Add Speaker</Title>

				<Label>Name: </Label>
				<InputText
					type="text"
					value={name}
					onChange={this.handleChangeInputName}
				/>
				<Label>Age: </Label>
				<InputText
					type="numper"
					value={age}
					onChange={this.handleChangeInputName}
				/>
				<Label>User Name: </Label>
				<InputText
					type="text"
					value={UserName}
					onChange={this.handleChangeInputName}
				/>
				<Label>Email: </Label>
				<InputText
					type="text"
					value={email}
					onChange={this.handleChangeInputName}
				/>
				<Label>Password: </Label>
				<InputText
					type="text"
					value={Password}
					onChange={this.handleChangeInputName}
				/>
				<Label> Address </Label>
				<Label>city: </Label>
				<InputText
					type="text"
					value={Address.city}
					onChange={this.handleChangeInputName}
				/>
				<Label>street: </Label>
				<InputText
					type="text"
					value={Address.street}
					onChange={this.handleChangeInputName}
				/>
				<Label>building: </Label>
				<InputText
					type="text"
					value={Address.building}
					onChange={this.handleChangeInputName}
				/>
				
				<Button onClick={this.handleIncludeSpeaker}>Add Movie</Button>
				<CancelButton href={"/speakers/list"}>Cancel</CancelButton>
			</Wrapper>
		);
	}
}

export default SpeakersInsert;
